'use strict';

/* jslint indent: 4 */

var fs = require('fs');

exports.readJSONFile = function(filename) {
    var file;

    try {
        file = JSON.parse(fs.readFileSync(filename, 'utf8'));
    } catch (error) {
        throw new Error('Could not parse JSON file "' + filename + '": ' + error);
    }

    return file;
};
